import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Dashboard = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState(null);
  const [stats, setStats] = useState({
    skills: 0,
    projects: 0,
    work: 0,
    links: 0
  });
  const [topSkills, setTopSkills] = useState([]);
  const [recentProjects, setRecentProjects] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);

  const { user } = useAuth();

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const [skillsRes, projectsRes, workRes, linksRes, topSkillsRes] = await Promise.all([
        axios.get('/api/skills'),
        axios.get('/api/projects?limit=3'),
        axios.get('/api/work'),
        axios.get('/api/links'),
        axios.get('/api/skills/top?limit=5')
      ]);

      setStats({
        skills: skillsRes.data.data.count,
        projects: projectsRes.data.data.pagination.totalCount,
        work: workRes.data.data.count,
        links: linksRes.data.data.count
      });

      setTopSkills(topSkillsRes.data.data.skills);
      setRecentProjects(projectsRes.data.data.projects);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    setSearchLoading(true);
    try {
      const response = await axios.get(`/api/search?q=${encodeURIComponent(searchQuery)}`);
      setSearchResults(response.data.data);
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setSearchLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="loading">
        <div className="spinner"></div>
        <p>Loading dashboard...</p>
      </div>
    );
  }

  return (
    <div>
      <div className="dashboard-hero">
        <div className="hero-content">
          <h1 className="hero-title">Welcome back, {user?.name}! ðŸš€</h1>
          <p className="hero-subtitle">Ready to showcase your amazing work on MY-API-PLAYGROUND?</p>
        </div>
      </div>

      <div className="card">
        {/* Search */}
        <form onSubmit={handleSearch} style={{ marginBottom: '2rem' }}>
          <div className="form-group">
            <label htmlFor="search">Search your MY-API-PLAYGROUND</label>
            <div style={{ display: 'flex', gap: '1rem' }}>
              <input
                type="text"
                id="search"
                className="form-control"
                placeholder="Search skills, projects, work experience..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
              <button type="submit" className="btn btn-primary" disabled={searchLoading}>
                {searchLoading ? 'Searching...' : 'Search'}
              </button>
            </div>
          </div>
        </form>

        {/* Search Results */}
        {searchResults && (
          <div className="card" style={{ marginBottom: '2rem' }}>
            <div className="card-header">
              <h3>Search Results for "{searchResults.query}"</h3>
              <p>Found {searchResults.summary.totalResults} results</p>
            </div>

            {searchResults.results.skills.length > 0 && (
              <div style={{ marginBottom: '1rem' }}>
                <h4>Skills ({searchResults.results.skills.length})</h4>
                <div className="grid grid-2">
                  {searchResults.results.skills.map(skill => (
                    <div key={skill._id} className="skill-item">
                      <div>
                        <div className="skill-name">{skill.name}</div>
                        <div className="skill-category">{skill.category}</div>
                      </div>
                      <div className="skill-proficiency">{skill.proficiency}/10</div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {searchResults.results.projects.length > 0 && (
              <div style={{ marginBottom: '1rem' }}>
                <h4>Projects ({searchResults.results.projects.length})</h4>
                {searchResults.results.projects.map(project => (
                  <div key={project._id} className="project-item" style={{ marginBottom: '1rem' }}>
                    <div className="project-title">{project.title}</div>
                    <div className="project-description">{project.description}</div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>

      {/* Stats Overview */}
      <div className="grid grid-2" style={{ marginBottom: '2rem' }}>
        <div className="card">
          <h3>MY-API-PLAYGROUND Stats</h3>
          <div className="grid grid-2">
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#3498db', margin: '0' }}>{stats.skills}</h2>
              <p>Skills</p>
              <Link to="/skills" className="btn btn-outline">Manage Skills</Link>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#27ae60', margin: '0' }}>{stats.projects}</h2>
              <p>Projects</p>
              <Link to="/projects" className="btn btn-outline">View Projects</Link>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#e67e22', margin: '0' }}>{stats.work}</h2>
              <p>Work Experience</p>
              <Link to="/work" className="btn btn-outline">Manage Work</Link>
            </div>
            <div style={{ textAlign: 'center' }}>
              <h2 style={{ color: '#9b59b6', margin: '0' }}>{stats.links}</h2>
              <p>Links</p>
              <Link to="/links" className="btn btn-outline">Manage Links</Link>
            </div>
          </div>
        </div>

        <div className="card">
          <h3>Top Skills</h3>
          {topSkills.length > 0 ? (
            <div>
              {topSkills.map(skill => (
                <div key={skill._id} className="skill-item">
                  <div>
                    <div className="skill-name">{skill.name}</div>
                    <div className="skill-category">{skill.category}</div>
                  </div>
                  <div className="skill-proficiency">{skill.proficiency}/10</div>
                </div>
              ))}
            </div>
          ) : (
            <p>No skills added yet. <Link to="/skills">Add your first skill</Link></p>
          )}
        </div>
      </div>

      {/* Recent Projects */}
      <div className="card">
        <div className="card-header">
          <h3>Recent Projects</h3>
          <Link to="/projects" className="btn btn-primary">View All Projects</Link>
        </div>

        {recentProjects.length > 0 ? (
          <div className="grid grid-2">
            {recentProjects.map(project => (
              <div key={project._id} className="project-item">
                <div className="project-title">{project.title}</div>
                <div className="project-description">
                  {project.description.length > 100 
                    ? `${project.description.substring(0, 100)}...` 
                    : project.description
                  }
                </div>
                {project.skills && project.skills.length > 0 && (
                  <div className="project-skills">
                    {project.skills.slice(0, 3).map((skill, index) => (
                      <span key={index} className="skill-tag">{skill}</span>
                    ))}
                    {project.skills.length > 3 && (
                      <span className="skill-tag">+{project.skills.length - 3} more</span>
                    )}
                  </div>
                )}
              </div>
            ))}
          </div>
        ) : (
          <p>No projects added yet. <Link to="/projects">Add your first project</Link></p>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
